function getSpecialityNameFromId(id){
  for(var eachSpeciality of specialityListing){
    if(eachSpeciality.id == id) {
      return eachSpeciality.title
    }
  }
}
function showHospitalNameFromId(id) {
  for (var eachHospital of hospitalListing) {
    if (eachHospital.id == id) {
      return eachHospital.hospital_name;
    }
  }
}
console.log(showHospitalNameFromId(10));
